mapper.select:
select count(id)
from dcd_user_activity
where account = #{account} and activityID = #{activityID}

select count(id)
from dcd_user_activity
where activityID = #{activityID}

select u.id, u.account, u.truename, u.icon, u.gender, u.nickname, u.introduction, u.fellowship, ua.activityID
from dcd_user u, dcd_user_activity ua
where u.account = ua.account and ua.activityID = #{activityID}

select a*, ua.account
from dcd_activity a, dcd_user_activity ua
where a.id = ua.activityID and ua.account = #{account}
