mapper.select:
select u.id, u.account, u.truename, u.icon, u.gender, u.nickname, u.introduction, u.fellowship, uac.articleID
from dcd_user u, dcd_user_article_collection uac
where u.account = uac.account and uac.articleID = #{articleID}

select a.*, uac.account, #{account} as isCollectionAccount
from dcd_article a, dcd_user_article_collection uac
where a.id = uac.articleID and uac.account = #{account}

select count(id)
from dcd_user_article_collection
where articleID = #{articleID} and account = #{account}
