mapper.select:
select u.id, u.account, u.truename, u.icon, u.gender, u.nickname, u.introduction, u.fellowship, uyc.prayID
from dcd_user u, dcd_user_pray_collection uyc
where u.account = uyc.account and uyc.prayID = #{prayID}

select p.*, uyc.account, #{account} as isCollectionAccount
from dcd_pray p, dcd_user_pray_collection uyc
where p.id = uyc.prayID and uyc.account = #{account}

select count(id)
from dcd_user_pray_collection
where prayID = #{prayID} and account = #{account}
